private  String  dept_name;
private  String  dept_remark;
public Dept() {
	super();
	// TODO Auto-generated constructor stub
}
public Integer getDept_id() {
	return dept_id;
}
public void setDept_id(Integer dept_id) {
	this.dept_id = dept_id;
}
